$bleu: #09021e;
$cyan: #4cc9f0;
$magenta: #f72585;

* {
    box-sizing: border-box;
    cursor: none!important;
}

body {
    height: 100vh;
    margin: 0;
    overflow: hidden;
    background: $bleu;
}

canvas {
    display: block;
    position: absolute;
    z-index: -1;
    &.hologramActive {
        transition: mix-blend-mode 2s cubic-bezier(.25, 0, 0, 1);
        // mix-blend-mode: difference;
        // opacity: 0.5;
        animation: blinkCanvas 6s infinite;
    }
    &.hologramDefault {
        transition: mix-blend-mode 2s cubic-bezier(.25, 0, 0, 1);
        mix-blend-mode: normal;
        // opacity: 1;
        animation: none;
    }
    @keyframes blinkCanvas {
        30% {
            opacity: 1;
        }
        32% {
            opacity: .7;
        }
        33% {
            opacity: 1;
        }
        35% {
            opacity: .7;
        }
        40% {
            opacity: 1;
        }
        80% {
            opacity: .7;
        }
        82% {
            opacity: 1;
        }
        85% {
            opacity: .8;
        }
        87% {
            opacity: 1;
        }
    }
}


//CURSOR CUSTOM///
.cursor {
    position: fixed;
    // top: 0;
    // left: 0;
    pointer-events: none;
    z-index: 999;   
    mix-blend-mode: difference;
    &.mouseover {
        transition: mix-blend-mode .75s cubic-bezier(.25, 0, 0, 1);
        mix-blend-mode: normal;
    }
}

.cursor-shape_in {
	position: absolute;
	pointer-events: none;
	border-radius: 50px;
	left: -10px;
    top: -10px;
    width: 20px;
    height: 20px;
    background: $cyan;
    transition: filter .75s cubic-bezier(.25, 0, 0, 1);
    &.mouseover {
        // padding: 30px;
        transition: filter .75s cubic-bezier(.25, 0, 0, 1);
        // opacity: 0;
        filter: blur(20px);
    }
}

.cursor-shape_out {
    mix-blend-mode: difference;
	z-index: 99;
	position: absolute;
	pointer-events: none;
    border-radius: 50px;
	left: 0;
	top: 0;
    width: 50px;
    height: 50px;
    border: $cyan solid 1px;
    transition: padding .75s cubic-bezier(.25, 0, 0, 1);
    animation: blinkTitle 8s infinite ease-in-out alternate;
}

main {
    position: fixed;
    width: 100%;
    height: 100vh;
    .homeContainer {
        display: flex;
        align-items: center;
        justify-content: center;
        flex-direction: column;
        height: 100vh;
        width: 100%;
        position: fixed;
        .open {
            display: none;
        }
        .close {
            display: flex;
        }
        .title {
            width: 50vw;
            animation: blinkTitle 8s infinite ease-in-out alternate;
        }
        .littleTitle {
            width: 7vw;
            position: absolute;
            right: 2vw;
            top: 2.2vh;
        }
        .title {
            path,
            line {
                fill: none;
                stroke: #4CC9F0;
                stroke-width: 0.5;
                stroke-miterlimit: 10;
                stroke-dashoffset: -1000;
                stroke-dasharray: 1000;
            }
        }
        .pathTitleIn {
            animation: titleIn 10s forwards ease-in-out;
        }
        .pathLineIn {
            animation: lineIn 8s forwards ease-in-out;
        }
        .pathTitleOut {
            animation: titleOut 8s forwards ease-in-out;
        }
        .pathLineOut {
            animation: lineOut 7.5s forwards ease-in-out;
        }
        @keyframes titleIn {
            0% {
                stroke-dashoffset: -1000;
            }
            40% {
                stroke-dashoffset: 0;
                fill: transparent;
            }
            45% {
                fill: $cyan;
            }
            70% {
                fill: $cyan;
            }
            100% {
                fill: $cyan;
                stroke-dashoffset: 0;
            }
        }
        @keyframes titleOut {
            0% {
                stroke-dashoffset: 0;
                fill: $cyan;
            }
            10% {
                fill: transparent;
            }
            80% {
                stroke-dashoffset: -1000;
                fill: transparent;
            }
            85% {
                fill: transparent;
            }
            100% {
                fill: transparent;
                stroke-dashoffset: -1000;
            }
        }
        @keyframes lineIn {
            0% {
                stroke-dashoffset: -1000;
                stroke-width: 0.5px;
            }
            40% {
                stroke-dashoffset: 0;
                fill: transparent;
                stroke-width: 0.5px;
            }
            45% {
                fill: transparent;
                stroke-width: 0.5px;
            }
            70% {
                stroke-width: 4px;
                stroke-dashoffset: 0;
            }
            100% {
                stroke-width: 4px;
                stroke-dashoffset: 0;
            }
        }
        @keyframes lineOut {
            0% {
                stroke-dashoffset: 0;
                stroke-width: 4px;
            }
            10% {
                stroke-width: 0.5px;
            }
            80% {
                stroke-dashoffset: -1000;
                stroke-width: 0.5px;
            }
            85% {
                stroke-width: 0.5px;
            }
            100% {
                stroke-width: 0.5px;
                stroke-dashoffset: -1000;
            } 
        }
        @keyframes blinkTitle {
            30% {
                opacity: 1;
                filter: drop-shadow(0 0 5px $cyan);
                // filter: blur(5px);
            }
            33% {
                opacity: 1;
                filter: drop-shadow(0 0 2px $cyan);
                // filter: blur(2px);
            }
            35% {
                opacity: 1;
                filter: drop-shadow(0 0 3px $cyan);
            }
            80% {
                opacity: 1;
                filter: drop-shadow(0 0 1px $cyan);
            }
            100% {
                opacity: 1;
            }
        }
    }
}

.btn {
    height: 6vh;
    outline: none;
    border: none;
    font-family: "littleTitle";
    font-size: 1.85vh;
    letter-spacing: .15vw;
    background: $bleu;
    color: $cyan;
    border: 1px solid $cyan;
    &__start {
        width: 300px;
        height: 57px;
        overflow: hidden;
        position: relative;
        transition: .75s cubic-bezier(.25, 0, 0, 1);
        &:hover {
            background: $cyan;
            color: $bleu;
            border: 1px solid $bleu;
        }
        .spanContainerStartMouseout.neonText {
            animation: blinkBtn 4s infinite alternate;
        }
        .spanContainerStartMouseover,
        .spanContainerStartMouseout {
            position: absolute;
            transform: translate(-50%, -50%);
            top: 50%;
            left: 50%;
            width: 100%;
            height: 100%;
            clip-path: inset(35% 0 35% 0);
        }
        .spanContainerStartMouseover span,
        .spanContainerStartMouseout span {
            display: inline-block;
            line-height: 6vh;
        }
        .spanContainerStartMouseout span {
            transform: translateY(40px);
        }
    }
    &__backHome {
        width: 8vw;
        top: 2%;
        left: .5%;
        position: absolute;
        z-index: 2;
        clip-path: inset(0% 100% 0% 0%);
        &.open {
            pointer-events: all;
        }
        &.close {
            pointer-events: none;
        }
        overflow: hidden;
        transition: .75s cubic-bezier(.25, 0, 0, 1);
        &:hover {
            background: $cyan;
            color: $bleu;
            border: 1px solid $bleu;
        }
        .spanContainerBackMouseout.neonText {
            animation: blinkBtn 4s infinite alternate;
        }
        .spanContainerBackMouseover,
        .spanContainerBackMouseout {
            position: absolute;
            transform: translate(-50%, -50%);
            top: 50%;
            left: 50%;
            width: 100%;
            height: 100%;
            clip-path: inset(35% 0 35% 0);
        }
        .spanContainerBackMouseover span,
        .spanContainerBackMouseout span {
            display: inline-block;
            line-height: 6vh;
        }
        .spanContainerBackMouseout span {
            transform: translateY(40px);
        }
    }
}

////GRID WITH INTERACTIONS///
.colContainer {
    position: absolute;
    width: 100%;
    height: 100vh;
    z-index: -1;
    .col {
        clip-path: inset(100% 0% 0% 0%);
    }
}

.rowContainer {
    position: absolute;
    width: 100%;
    height: 100vh;
    z-index: -1;
    .row {
        clip-path: inset(0% 0% 0% 100%);
    }
}

.row {
    width: 100%;
    height: 2px;
    background: #0d0437; //4cc9f0
    margin-top: 20px;
    opacity: .65;
    &.line {
        width: 0;
        position: absolute;
        height: 2px;
        background: #1b355e;
        z-index: -1;
        opacity: 0.35;
        &__1 {
            bottom: 6.1%;
        }
        &__2 {
            bottom: 1.8%;
        }
    }
}

.col {
    width: 2px;
    height: 100vh;
    background: #0d0437;
    margin-right: 10px;
    margin-left: 10px;
    display: inline-block;
    opacity: .65;
    &.line {
        width: 2px;
        height: 0;
        background: #1b355e;
        position: absolute;
        z-index: -1;
        opacity: 0.35;
        &__1 {
            right: 0;
        }
        &__2 {
            right: 0;
        }
    }
}

////SOCIALS MEDIA///
.sm {
    width: 40px;
    height: 4.3%;
    position: absolute;
    right: .9%;
    color: $cyan;
    display: flex;
    justify-content: center;
    align-items: center;
    z-index: 2;
    font-family: 'littleTitle';
    letter-spacing: 2px;
    overflow: hidden;
    font-weight: 600;
    &.neonText {
        text-shadow: 0px;
        animation: blink 4s infinite alternate;
    }
    @keyframes blink {
        30% {
            opacity: 1;
            text-shadow: 0 0 0 $cyan, 0 0 2px $cyan, 0 0 2px $cyan;
        }
        32% {
            opacity: .25;
        }
        33% {
            opacity: 1;
        }
        35% {
            opacity: .25;
        }
        40% {
            opacity: 1;
        }
    }
    @keyframes blinkBtn {
        30% {
            opacity: 1;
            text-shadow: 0 0 0 $bleu, 0 0 2px $bleu, 0 0 1px $bleu;
        }
        32% {
            opacity: .25;
        }
        33% {
            opacity: 1;
        }
        35% {
            opacity: .25;
        }
        40% {
            opacity: 1;
        }
    }
    @keyframes inSM {
        to {
            transform: translateX(0);
        }
    }
    @keyframes outSM {
        to {
            transform: translateX(165%);
        }
    }
    @keyframes outImpairSM {
        to {
            transform: translateX(-165%);
        }
    }
    &__1 {
        bottom: .9%;
        &.mouseover::after {
            content: "FB";
            position: absolute;
            animation: outSM .5s ease forwards;
        }
        &.mouseover::before {
            content: "FB";
            position: absolute;
            transform: translateX(-165%);
            animation: inSM .5s ease forwards;
        }
        &.mouseout::after {
            content: "FB";
            position: absolute;
            animation: inSM .5s ease forwards;
            transform: translateX(-165%);
        }
        &.mouseout::before {
            content: "FB";
            position: absolute;
            animation: outSM .5s ease forwards;
            transform: translateX(0);
        }
    }
    &__2 {
        bottom: 5.5%;
        &.mouseover::after {
            content: "IG";
            position: absolute;
            animation: outImpairSM .5s ease forwards;
        }
        &.mouseover::before {
            content: "IG";
            position: absolute;
            transform: translateX(165%);
            animation: inSM .5s ease forwards;
        }
        &.mouseout::after {
            content: "IG";
            position: absolute;
            animation: inSM .5s ease forwards;
            transform: translateX(165%);
        }
        &.mouseout::before {
            content: "IG";
            position: absolute;
            animation: outImpairSM .5s ease forwards;
            transform: translateX(0);
        }
    }
    &__3 {
        bottom: 10.1%;
        &.mouseover::after {
            content: "IN";
            position: absolute;
            animation: outSM .5s ease forwards;
        }
        &.mouseover::before {
            content: "IN";
            position: absolute;
            transform: translateX(-165%);
            animation: inSM .5s ease forwards;
        }
        &.mouseout::after {
            content: "IN";
            position: absolute;
            animation: inSM .5s ease forwards;
            transform: translateX(-165%);
        }
        &.mouseout::before {
            content: "IN";
            position: absolute;
            animation: outSM .5s ease forwards;
            transform: translateX(0);
        }
    }
}

