import * as THREE from "three";
import { TweenMax as TM } from "gsap";
import vertexShader from "./libs/glsl/vertexShader.glsl";
import fragmentShader from "./libs/glsl/fragmentShader.glsl";

export default class PlaneWorkshop {
    constructor(scene, img, imgBW, cb) {
        this.$image = img
        this.$image2 = imgBW
        // this.$image = atelier1;
        // this.$image2 = atelier2;
        this.scene = scene;
        this.callback = cb;

        this.loader = new THREE.TextureLoader();

        this.image = this.loader.load(this.$image.src, () => {
            this.start();
        });
        this.hover = this.loader.load(this.$image2.src);
        // this.$image.style.opacity = 0;
        this.sizes = new THREE.Vector2(0, 0);
        this.offset = new THREE.Vector2(0, 0);

        this.mouse = new THREE.Vector2(0, 0);
        window.addEventListener("mousemove", (ev) => {
            this.onMouseMove(ev);
        });

        // this.meshFinal = this.createMesh(this.mesh);
        // console.log(this.meshFinal)
        // console.log(this.createMesh(this.mesh))
    }

    start() {
        // this.getSizes();
        this.createMesh();
        this.callback();
    }

    

    // getSizes() {
    //     const { width, height, top, left } = this.$image.getBoundingClientRect();

    //     this.sizes.set(width, height);

    //     this.offset.set(
    //         left - window.innerWidth / 2 + width / 2,
    //         -top + window.innerHeight / 2 - height / 2
    //     );
    // }

    createMesh() {
        this.uniforms = {
            u_image: {
                type: "t",
                value: this.image
            },
            u_imagehover: {
                type: "t",
                value: this.hover
            },
            u_mouse: {
                value: this.mouse
            },
            u_time: {
                value: 0
            },
            u_res: {
                value: new THREE.Vector2(window.innerWidth, window.innerHeight)
            }
        };

        this.plane = new THREE.PlaneBufferGeometry(1.6 / 1.2, .9 / 1.2, 1, 1);
        this.materialPlane = new THREE.ShaderMaterial({
            uniforms: this.uniforms,
            vertexShader: vertexShader,
            fragmentShader: fragmentShader,
            defines: {
                PR: window.devicePixelRatio.toFixed(1)
            },
            side: THREE.DoubleSide
        });

        this.mesh = new THREE.Mesh(this.plane, this.materialPlane);

        this.mesh.position.set(this.offset.x, this.offset.y, 0);
        this.mesh.scale.set(this.sizes.x, this.sizes.y, 1);

        this.scene.add(this.mesh);

        this.mesh.position.y = 17;
        this.mesh.rotation.y = -Math.PI / 2;

        return this.mesh
    }

    onMouseMove(event) {
        TM.to(this.mouse, 0.5, {
            x: (event.clientX / window.innerWidth) * 2 - 1,
            y: -(event.clientY / window.innerHeight) * 2 + 1
        });

        // TM.to(this.mesh.rotation, 0.2, {
        //     x: -this.mouse.y * 0.1,
        //     y: this.mouse.x * (Math.PI / 15)
        // });
    }

    update() {
        this.uniforms.u_time.value += 0.005;
    }
}

/////// PLANES MESHS ///////
