import * as THREE from "three";

import PlaneWorkshop from './Plane'
import Models from './Objects'

import socleModel from "../assets/model/base.glb";
import logoModel from "../assets/model/logo.glb";

import atelier1 from "../assets/img/atelier1.png";
import atelier2 from "../assets/img/atelier2.png";
import atelier3 from "../assets/img/atelier3.jpg";
import atelier4 from "../assets/img/atelier4.jpg";
import atelier5 from "../assets/img/atelier5.png";
import atelier6 from "../assets/img/atelier6.png";
import atelier7 from "../assets/img/atelier7.jpg";
import atelier8 from "../assets/img/atelier8.png";

import particle from "../assets/img/particle.png";

const perspective = 800
let variation = 0;

export default class Scene {
    constructor() {
        this.container = document.querySelector('#mainStage')

        this.scene = new THREE.Scene()
        this.renderer = new THREE.WebGLRenderer({
            antialias: true,
            canvas: this.container,
            alpha: true
        })

        this.renderer.setSize(window.innerWidth, window.innerHeight)
        this.renderer.setPixelRatio(window.devicePixelRatio)

        this.createPivot();
        this.initLights()
        this.initCamera()
        this.resize()
        this.particles()

        this.plane14 = new PlaneWorkshop(this.scene, atelier1, atelier2, () => {
            this.update()
        })

        this.socle = new Models(this.scene, "socle", "socleLoader", socleModel, () => {
            this.update()
        })
        this.logo = new Models(this.scene, "logo", "logoLoader", logoModel, () => {
            this.update()
        })
    }

    createPivot() {

        /////// PLANE AXES ///////
        this.planeAxe = new THREE.Object3D();
        this.planeAxe.position.y = -21.5
        this.scene.add(this.planeAxe);

        /////// PLANES PIVOTS ///////
        this.pivot1 = new THREE.Object3D();
        this.pivot2 = new THREE.Object3D();
        this.pivot3 = new THREE.Object3D();
        this.pivot4 = new THREE.Object3D();
        this.pivot5 = new THREE.Object3D();
        this.pivot6 = new THREE.Object3D();
        this.pivot7 = new THREE.Object3D();
        this.pivot8 = new THREE.Object3D();
        this.pivot9 = new THREE.Object3D();
        this.pivot10 = new THREE.Object3D();
        this.pivot11 = new THREE.Object3D();
        this.pivot12 = new THREE.Object3D();
        this.pivot13 = new THREE.Object3D();
        this.pivot14 = new THREE.Object3D();

        this.pivot1.position.z = 2.25;
        this.pivot2.position.x = -2.25;
        this.pivot3.position.z = -2.25;
        this.pivot4.position.x = 2.25;
        this.pivot5.position.z = 2.25;
        this.pivot6.position.x = -2.25;
        this.pivot7.position.z = -2.25;
        this.pivot8.position.x = 2.25;
        this.pivot9.position.z = 2.25;
        this.pivot10.position.x = -2.25;
        this.pivot11.position.z = -2.25;
        this.pivot12.position.x = 2.25;
        this.pivot13.position.z = 2.25;
        this.pivot14.position.x = -2.25;

        // this.pivot14.add(this.plane14.createMesh());
        console.log(this.plane14.createMesh())


        this.planeAxe.add(this.pivot1, this.pivot2, this.pivot3, this.pivot4, this.pivot5, this.pivot6, this.pivot7, this.pivot8, this.pivot9, this.pivot10, this.pivot11, this.pivot12, this.pivot13, this.pivot14);
    }

    particles() {
        /////// PARTICLES ///////
        this.particleGeo = new THREE.Geometry();
        for (let i = 0; i < 1000; i++) {
            let particle = new THREE.Vector3(
                Math.random() * 18 - 9,
                Math.random() * 18 - 9,
                Math.random() * 3.4 - 1.7)
            this.particleGeo.vertices.push(particle);
        }

        this.particleMaterial = new THREE.PointsMaterial({
            size: 0.018,
            map: new THREE.TextureLoader().load(particle),
            blending: THREE.AdditiveBlending,
            transparent: true,
            opacity: .5
        });

        this.particleMesh = new THREE.Points(this.particleGeo, this.particleMaterial);
        this.scene.add(this.particleMesh);
    }
    
    initLights() {
        /////// LIGHTS ///////
        this.targetLogo = new THREE.Object3D();
        this.targetLogo.position.set(0, 0, 0)
        this.scene.add(this.targetLogo);

        this.lightRight = new THREE.DirectionalLight(magentaColorReset, .75);
        this.lightRight.position.set(7, .5, 7)
        this.lightRight.target = this.targetLogo;
        this.scene.add(this.lightRight);

        this.lightLeft = new THREE.DirectionalLight(magentaColorReset, .75);
        this.lightLeft.position.set(-7, .5, 7)
        this.lightLeft.target = this.targetLogo;
        this.scene.add(this.lightLeft);

        this.lightCenter = new THREE.DirectionalLight(cyanColorReset, 8);
        this.lightCenter.position.set(0, -1.5, 0)
        this.lightCenter.target = this.targetLogo;
        this.scene.add(this.lightCenter)

        this.lightCenterSocle = new THREE.PointLight(cyanColorReset, 18, .75);
        this.lightCenterSocle.position.set(0, -1.5, 0)
        this.scene.add(this.lightCenterSocle)

        const magentaColor = new THREE.Color(0xf72585);
        const cyanColor = new THREE.Color(0x4cc9f0);
        const magentaColorReset = new THREE.Color(0x33081b);
        const cyanColorReset = new THREE.Color(0x18404d);
    }

    initCamera() {
        this.camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        this.camera.position.set(0, 0, 35);
    }

    update() {
        requestAnimationFrame(this.update.bind(this))

        this.plane.update()

        
        if (this.camera.position.z > 2.9) { //START LOGO ROTATION ON CLICK
            this.logo.rotation.y += .01
            if (this.logo.rotation.y > Math.PI) {
                this.logo.rotation.y -= Math.PI * 2
            }
        }
        
        this.particleGeo.vertices.forEach(p => { //PARTICLES ANIMATION
            p.y += 0.0025;
            variation += 0.000005;
            p.x += Math.sin(variation) / 1000
            if (p.y > 9) {
                p.y = -9;
            }
        });
        
        this.particleGeo.verticesNeedUpdate = true;
        
        this.renderer.render(this.scene, this.camera)
    }

    resize() {
        window.addEventListener('resize', () => {
            this.renderer.setSize(window.innerWidth, window.innerHeight);

            this.camera.aspect = window.innerWidth / window.innerHeight;
            this.camera.updateProjectionMatrix();

        })
    }
}


////////// SCENE //////////
